import React, { useState } from "react";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import styles from "./AddUser.module.css";

/**
 *
 * @param {*} props
 * @returns
 */
function AddUser(props) {
  const [username, validateUsername] = useState();
  const [age, validateAge] = useState();
  const [userDetails, addUserToList] = useState();
  let isUsernameValid = false;
  let isAgeValid = false;
  let isFormEmpty = true;

  /**
   *
   * @param {*} event
   */
  const updateUsernameHandler = (event) => {};

  /**
   *
   * @param {*} event
   */
  const updateAgeHandler = (event) => {};

  /**
   *
   */
  const addUserHandler = (event) => {
    event.preventDefault();
  };

  return (
    <form onSubmit={addUserHandler}>
      <label class={styles.input.label} htmlFor="username">Username</label>
      <input
        id="username"
        class={styles.input.input}
        type="text"
        onUpdate={updateUsernameHandler}
      ></input>
      <label class={styles.input.label} htmlFor="age">Age (Years)</label>
      <input
        id="age"
        class={styles.input.input}
        type="number"
        onUpdate={updateAgeHandler}
      ></input>
      <button type="submit">Add User</button>
    </form>
  );
}

export default AddUser;
