import React, { useState } from "react";
import AddUser from "./components/User/AddUser";
import UserList from "./components/User/UserList";

function App() {
  const [existingUsers, updateExistingUsers] = useState([]);

  const addUserHandler = (name, age) => {
    console.log("Adding new user... ", name, age)
    let id = Math.random();
    let newUser = {
      key: id,
      name: name,
      age: age
    }
    updateExistingUsers((prevUsers) => {
      return prevUsers.unshift(newUser);
    });
  };

  const removeUserHandler = (event) => {
    // updateUsers((prevUsers) => {
    //   prevUsers.filter((uidExisting) => {
    //     return uidToRemove !== uidExisting;
    //   });
    // });
  };

  return (
    <React.Fragment>
      <AddUser onAddUser={addUserHandler} />
      <UserList users={existingUsers} onRemoveUser={removeUserHandler} />
    </React.Fragment>
  );
}

export default App;
