  const addUserHandler = (event) => {
    event.preventDefault();
  };

  return (
    <form onSubmit={addUserHandler}>
      <label class={styles.input.label} htmlFor="username">Username</label>
      <input
        id="username"
        class={styles.input.input}
        type="text"
      ></input>
      <label class={styles.input.label} htmlFor="age">Age (Years)</label>
      <input
        id="age"
        class={styles.input.input}
        type="number"
      ></input>
      <button type="submit">Add User</button>
    </form>
  );
}

export default AddUser;
